package operators;

public class discountpercent {

	@SuppressWarnings("unused")
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double fees=125000;
		double discountpercent=125000/100*10;
		
		double actualfees=fees-discountpercent;
		System.out.println("fees to be paid is "+actualfees);
		
		
	}

}
